var isMBarStatic = [IF IsMBarStatic]true[ENDIF IsMBarStatic][IFNOT IsMBarStatic]false[ENDIF IsMBarStatic];

var isOpen = [IF IsMBarStatic]true[ENDIF IsMBarStatic][IFNOT IsMBarStatic]false[ENDIF IsMBarStatic];


function alternMBar() {

  if (isMBarStatic) {

    isMBarStatic = false;
    isOpen = true;
    document.getElementById('wdcmd').style.display = 'none';
    document.getElementById('wditmbar').style.display = 'block';

  } else {

    isMBarStatic = true;
    isOpen = true;
    document.getElementById('wdcmd').style.display = 'block';
    document.getElementById('wditmbar').style.display = 'hidden';
  }
  computefbodywh();
}

function computefbodywh() {

  var bodH = getFrameHeight();
  var bodW = getFrameWidth();
  var topH = getObjectHeight(document.getElementById('wdtitle'));
  var wor = (isOpen ? 0.2 : 0.0);
  var wdrift = 0.02;

  var md = document.getElementById('wdmenu');
  md.style.heigth = 'auto';

  if (isMBarStatic) {

    md.style.position = 'relative';
    md.style.top = 0;
    md.style.left = 0;

    var cmdW = getObjectWidth(document.getElementById('wdcmd'));
    var ww = bodW - cmdW;
    document.getElementById('wdmenu').style.height = bodH - topH;
    document.getElementById('fbody').style.height = bodH - topH;
    document.getElementById('wdmenu').style.width = parseInt(ww * wor); 
    document.getElementById('wdcmd').style.width = cmdW; 
    document.getElementById('wdbody').style.width =
      document.getElementById('fbody').style.width = parseInt(ww * (1 - (wor + wdrift)));
    document.getElementById('wdmenu').style.display = (isOpen?'block':'none');

  } else {

    var md = document.getElementById('wdmenu');

    md.style.position = 'absolute';
    md.style.top = topH;
    md.style.width =  parseInt(bodW * wor);

    document.getElementById('fbody').style.height = bodH - topH;
    document.getElementById('wdbody').style.width =
      document.getElementById('fbody').style.width = parseInt(bodW);

    if (isOpen)  md.style.display = 'block';
    else md.style.display = 'none';

  }


}
